<template>  <header>    <!--<img src="@/assets/img/menu.png" alt="菜单">-->    <span>云链</span>    <p @click="goAddDevice">      <img src="@/assets/img/add.png" alt="add">    </p>  </header></template><script>  export default {    data() {      return{}    },    methods: {      goAddDevice(){        let res = this.isWifi();        if (!res) {          return false;        }        this.$router.push({path:'/addDevice'});      },      isWifi() {        const connection = navigator && navigator.connection;        if (connection.type !== 'wifi') {          this.$vux.alert.show({            title: '提示',            content: '当前网络环境不是Wi-Fi,请连接Wi-Fi才能添加设备!',            onShow() {            },            onHide() {            }          });          return false;        }        return true;      }    }  }</script><style lang="less" scoped>  header{    display: flex;    align-items: center;    padding: 0 20px;    height: 45px;    font-size: 18px;    background: #fff;    position: fixed;    top: 0;    left: 0;    right: 0;    z-index: 9;    span{      margin-left: auto;      transform: translate(30%);    }    p{      margin-left: auto;      width: 16px;height: 16px;      img{        display: block;        width: 100%;        height: 100%;      }    }  }</style>