<template>  <div class="block">    <header>      <span @click="goBack" class="arrow">←</span><span>我的</span>    </header>    <div class="back">      <div class="back_box"></div>    </div>    <section>      <span></span>      <div>        <h1>修改昵称</h1>      </div>      <x-input :max="11" :min="2" :show-clear="false" class="register_input" required v-model="nickname"></x-input>      <p @click="editUserInfo" class="submit cursor">提交</p>    </section>  </div></template><script>  import {XInput, XButton} from 'vux';  export default {    components: {      XInput, XButton    },    data() {      return {        nickname: ''      }    },    created() {      this.getUserInfo()    },    methods: {      // 返回上一页      goBack() {        this.$router.push('/my')      },      // 获取用户信息      getUserInfo() {        this.RestClient('GET_ONE', '/user/info').then((res) => {          if (res.data.code === 200) {            this.nickname = res.data.data.nickname;          }        })      },      // 修改信息      editUserInfo() {        if (this.nickname.length < 2) {          this.$vux.toast.text('最少需输入2个字符');          return false        }        let params = {          nickname: this.nickname,        };        this.RestClient('UPDATE', '/user/info', params).then((res) => {          if (res.data.code === 200) {            this.$vux.toast.text(res.data.message);            this.$router.push('/my')          }        })      },    }  }</script><style lang="less" scoped>  .block {    padding-top: 45px;    header {      color: #fff;      height: 47px;      display: flex;      padding: 0 20px;      align-items: center;      position: fixed;      left: 0;      right: 0;      top: 0;      background: rgb(22, 26, 55);      .arrow {        font-weight: bolder;        font-size: 24px;        margin-top: -5px;      }      span {        font-size: 17px;        margin-right: 14px;      }    }    .back {      height: 100px;      background: #000;      position: relative;      z-index: -1;      .back_box {        width: 90%;        background: #cccccc;        margin: 0 auto;        height: 40px;        position: absolute;        left: 5%;        top: 0;        border-radius: 20px;      }    }    section {      background: rgb(246, 247, 247);      border-top-left-radius: 30px;      border-top-right-radius: 30px;      padding: 10px 20px 40px;      display: flex;      flex-direction: column;      margin-top: -88px;      .iphone {        margin-top: 15px;        display: flex;        justify-content: space-between;        padding: 10px;        font-size: 16px;      }      p {        height: 50px;        color: #fff;        border-radius: 4px;        text-align: center;        line-height: 50px;        margin-top: 20px;        background: rgb(255, 196, 0);      }      .register_input {        margin-top: 10px;        background: #fff;      }    }  }  section > span {    margin: 0 auto 20px;    border-radius: 3px;    display: block;    width: 15%;    height: 5px;    background: rgb(229, 229, 229);  }  .weui-cell {    box-sizing: border-box;    padding: 16px 15px;  }  .weui-cell:before {    position: unset;  }  .weui-btn, .weui-btn_mini, .weui-btn_primary {    background: rgb(255, 196, 0);    position: absolute;    right: 0;    top: 0;    bottom: 0;  }</style>