<template>  <div class="block">    <header>      <span @click="goBack" class="arrow">←</span><span>提币记录</span>    </header>    <div class="back">      <div class="back_box"></div>    </div>    <div class="content">      <span></span>      <pull-to @infinite-scroll="getMoreList" class="file-lists">        <ul class="section_content" v-if="list.length">          <li :key="index" class="content_item" v-for="(item,index) in list">            <div class="content_item_div">              <div style="flex: 1">                <h3>数量</h3>                <p>{{item.totalAmount}}</p>              </div>              <div style="flex: 1">                <h3>状态</h3>                <p v-if="item.authStatus === 1">待审核</p>                <p v-if="item.authStatus === 2">通过</p>                <p v-if="item.authStatus === 3">未通过</p>              </div>              <div class="item_time" style="flex: 1">                <h3>时间</h3>                <p>{{item.createDate | formatime}}</p>              </div>            </div>            <p class="content_item_p" v-if="item.reason">未通过原因：{{item.reason}}</p>          </li>        </ul>        <div class="empty" v-else>          <img alt="无记录" src="@/assets/img/empty.png">          <p>暂无记录</p>        </div>      </pull-to>    </div>  </div></template><script>  import PullTo from 'vue-pull-to'  export default {    name: "TakeRecord",    components: {      PullTo    },    data() {      return {        list: [],        pageNo: 1,      }    },    mounted() {      this.getOutgoList();    },    methods: {      goBack() {        this.$router.push('/wallet')      },      getMoreList() {        this.pageNo++;        let params = {          pageNo: this.pageNo,          pageSize: '10'        };        this.RestClient('GET_LIST', '/wallet/outgo/list', params).then((res) => {          console.log(res)          if (res.data.code === 200) {            let pageData = [];            pageData = res.data.data.data;            this.list = this.list.concat(pageData)          }        })      },      // 获取提币记录      getOutgoList() {        let params = {          pageNo: this.pageNo,          pageSize: '10'        };        this.RestClient('GET_LIST', '/wallet/outgo/list', params).then((res) => {          if (res.data.code === 200) {            this.list = res.data.data.data          }        })      }    }  }</script><style scoped lang="less">  .block {    padding-top: 45px;    .back {      height: 100px;      background: rgb(22, 26, 55);      position: relative;      z-index: -1;      .back_box {        width: 90%;        background: #cccccc;        margin: 0 auto;        height: 40px;        position: absolute;        left: 5%;        top: 0;        border-radius: 20px;      }    }    header {      color: #fff;      height: 45px;      display: flex;      padding: 0 20px;      align-items: center;      position: fixed;      background: rgb(22, 26, 55);      left: 0;      right: 0;      top: 0;      z-index: 10;      .arrow {        font-weight: bolder;        font-size: 24px;        margin-top: -5px;      }      span {        font-size: 17px;        margin-right: 14px;      }    }  }  .section_content {    height: 600px;    font-size: 13px;    .content_item {      min-height: 50px;      .content_item_div {        display: flex;        justify-content: space-between;      }      .content_item_p {        /*max-height: 30px;*/        /*overflow: hidden;*/        color: #999999;        padding-top: 4px;      }      h3 {        font-size: 12px;        margin: 5px 0;        color: rgb(180, 186, 196);      }      .item_time {        text-align: right;      }    }  }  .content {    margin-top: -88px;    background: rgb(245, 246, 246);    border-top-left-radius: 30px;    border-top-right-radius: 30px;    padding: 10px 20px 40px;    min-height: 200px;    display: flex;    flex-direction: column;    .empty {      img {        display: block;        width: 250px;        height: 200px;        margin: 0 auto;      }      p {        color: rgb(139, 139, 139);        text-align: center;      }    }  }  .content > span {    margin: 0 auto 20px;    border-radius: 3px;    display: block;    width: 15%;    height: 5px;    background: rgb(229, 229, 229);  }  .block > p {    width: 90%;    height: 50%;    background: #ccc;    padding: 10px;    margin: 0 auto;    border-radius: 25px;  }</style>